                    and CREATOR like CONCAT('%', #{notice.creator}, '%')
                


                
                    and CREATOR_ID like CONCAT('%', #{notice.creatorId}, '%')
                


                
                    and LAST_UPDATE_ID like CONCAT('%', #{notice.lastUpdateId}, '%')
                


                
