select
ID, CREATED_TIME, CREATOR, CREATOR_ID, LAST_UPDATE_ID, LAST_UPDATE_TIME, LAST_UPDATER, USER_ID, USER_NAME,
USER_PHONE, SPEND_POINT, AUDIT_STATE, AUDIT_CONTENT, BANK_ID, BANK_NAME, BANK_IMG, CARD_ID, CARD_NAME,
ITEM_TYPE, ITEM_CONTENT, GET_MONEY, UNIT_PRICE, SERVICE_ID, SERVICE_NAME, SERVICE_NUMBER, SERVICE_IMG,
ODER_DETAIL, WX_PAYMENT_ID, WX_PAYMENT_AMOUNT, DELETED, ORDER_TYPE, SPARE2, SPARE3, SPARE4, SPARE5
from points_alliance.order_form

    
        and ID = #{id}
    
    
        and CREATED_TIME = #{createdTime}
    
    
        and CREATOR = #{creator}
    
    
        and CREATOR_ID = #{creatorId}
    
    
        and LAST_UPDATE_ID = #{lastUpdateId}
    
    
        and LAST_UPDATE_TIME = #{lastUpdateTime}
    
    
        and LAST_UPDATER = #{lastUpdater}
    
    
        and USER_ID = #{userId}
    
    
        and USER_NAME = #{userName}
    
    
        and USER_PHONE = #{userPhone}
    
    
        and SPEND_POINT = #{spendPoint}
    
    
        and AUDIT_STATE = #{auditState}
    
    
        and AUDIT_CONTENT = #{auditContent}
    
    
        and BANK_ID = #{bankId}
    
    
        and BANK_NAME = #{bankName}
    
    
        and BANK_IMG = #{bankImg}
    
    
        and CARD_ID = #{cardId}
    
    
        and CARD_NAME = #{cardName}
    
    
        and ITEM_TYPE = #{itemType}
    
    
        and ITEM_CONTENT = #{itemContent}
    
    
        and GET_MONEY = #{getMoney}
    
    
        and UNIT_PRICE = #{unitPrice}
    
    
        and SERVICE_ID = #{serviceId}
    
    
        and SERVICE_NAME = #{serviceName}
    
    
        and SERVICE_NUMBER = #{serviceNumber}
    
    
        and SERVICE_IMG = #{serviceImg}
    
    
        and ODER_DETAIL = #{oderDetail}
    
    
        and WX_PAYMENT_ID = #{wxPaymentId}
    
    
        and WX_PAYMENT_AMOUNT = #{wxPaymentAmount}
    
    
        and DELETED = #{deleted}
    
    
        and ORDER_TYPE = #{orderType}
    
    
        and SPARE2 = #{spare2}
    
    
        and SPARE3 = #{spare3}
    
    
        and SPARE4 = #{spare4}
    
    
        and SPARE5 = #{spare5}
    

order by CREATED_TIME DESC

