mapper.select:
select

from points_alliance.sys_user


    
        
            and ID like CONCAT('%', #{sysUser.id}, '%')
        
        
              and CREATED_TIME between #{beginTime} and #{endTime}
        
        
              and CREATOR like CONCAT('%', #{sysUser.creator}, '%')
        
        
              and CREATOR_ID like CONCAT('%', #{sysUser.creatorId}, '%')
        
        
              and LAST_UPDATE_ID like CONCAT('%', #{sysUser.lastUpdateId}, '%')
        
        
              and LAST_UPDATER like CONCAT('%', #{sysUser.lastUpdater}, '%')
        
        
              and NAME like CONCAT('%', #{sysUser.name}, '%')
        
        
              and PHONE like CONCAT('%', #{sysUser.phone}, '%')
        
        
              and ACCOUNT like CONCAT('%', #{sysUser.account}, '%')
        
        
              and PASSWORD like CONCAT('%', #{sysUser.password}, '%')
        
        
              and ROLE_ID = #{sysUser.roleId}
        
        
              and ROLE_NAME like CONCAT('%', #{sysUser.roleName}, '%')
        
        
              and PARENT_ID like CONCAT('%', #{sysUser.parentId}, '%')
        
        
              and COMPANY_ID = #{sysUser.companyId}
        
        
              and OPEN_ID like CONCAT('%', #{sysUser.openId}, '%')
        
        
              and INVITE_CODE like CONCAT('%', #{sysUser.inviteCode}, '%')
        
        
              and HEAD_URL like CONCAT('%', #{sysUser.headUrl}, '%')
        
        
            and USER_STATE = #{sysUser.userState}
        
        
            and MEMBER_LEVEL = #{sysUser.memberLevel}
        
        
            and COMPANY_LEVEL = #{sysUser.companyLevel}
        
        
            and DELETED = #{sysUser.deleted}
        
        
              and SPARE4 like CONCAT('%', #{sysUser.spare4}, '%')
        
        
              and SPARE5 like CONCAT('%', #{sysUser.spare5}, '%')
        
    



    
        
            and ID = #{sysUser.id}
        
        
              and CREATED_TIME between #{beginTime}
              and #{endTime}
        
        
              and CREATOR = #{sysUser.creator}
        
        
              and CREATOR_ID = #{sysUser.creatorId}
        
        
              and LAST_UPDATE_ID = #{sysUser.lastUpdateId}
        
        
              and LAST_UPDATE_TIME = #{sysUser.lastUpdateTime}
        
        
              and LAST_UPDATER = #{sysUser.lastUpdater}
        
        
              and NAME = #{sysUser.name}
        
        
              and PHONE = #{sysUser.phone}
        
        
              and ACCOUNT = #{sysUser.account}
        
        
              and PASSWORD = #{sysUser.password}
        
        
              and USER_STATE = #{sysUser.userState}
        
        
              and ROLE_ID = #{sysUser.roleId}
        
        
              and ROLE_NAME = #{sysUser.roleName}
        
        
              and PARENT_ID = #{sysUser.parentId}
        
        
              and COMPANY_ID = #{sysUser.companyId}
        
        
              and MEMBER_LEVEL = #{sysUser.memberLevel}
        
        
              and COMPANY_LEVEL = #{sysUser.companyLevel}
        
        
              and BALANCE = #{sysUser.balance}
        
        
              and CUMULATIVE_PROFIT = #{sysUser.cumulativeProfit}
        
        
              and CUMULATIVE_POINT = #{sysUser.cumulativePoint}
        
        
              and DELETED = #{sysUser.deleted}
        
        
              and OPEN_ID = #{sysUser.openId}
        
        
              and INVITE_CODE = #{sysUser.inviteCode}
        
        
              and HEAD_URL = #{sysUser.headUrl}
        
        
            and MEMBER_LEVEL = #{sysUser.memberLevel}
        
        
            and COMPANY_LEVEL = #{sysUser.companyLevel}
        
        
              and SPARE4 = #{sysUser.spare4}
        
        
              and SPARE5 = #{sysUser.spare5}
        
    

order by CREATED_TIME DESC
limit #{offset}, #{limit}

