select

from points_alliance.sys_user

    
        and ID = #{id}
    
    
          and CREATED_TIME = #{createdTime}
    
    
          and CREATOR = #{creator}
    
    
          and CREATOR_ID = #{creatorId}
    
    
          and LAST_UPDATE_ID = #{lastUpdateId}
    
    
          and LAST_UPDATE_TIME = #{lastUpdateTime}
    
    
          and LAST_UPDATER = #{lastUpdater}
    
    
          and NAME = #{name}
    
    
          and PHONE = #{phone}
    
    
          and ACCOUNT = #{account}
    
    
          and PASSWORD = #{password}
    
    
          and USER_STATE = #{userState}
    
    
          and ROLE_ID = #{roleId}
    
    
          and ROLE_NAME = #{roleName}
    
    
          and PARENT_ID = #{parentId}
    
    
          and COMPANY_ID = #{companyId}
    
    
          and MEMBER_LEVEL = #{memberLevel}
    
    
          and COMPANY_LEVEL = #{companyLevel}
    
    
          and BALANCE = #{balance}
    
    
          and CUMULATIVE_PROFIT = #{cumulativeProfit}
    
    
          and CUMULATIVE_POINT = #{cumulativePoint}
    
    
          and DELETED = #{deleted}
    
    
          and OPEN_ID = #{openId}
    
    
          and INVITE_CODE = #{inviteCode}
    
    
          and HEAD_URL = #{headUrl}
    
    
          and SPARE4 = #{spare4}
    
    
          and SPARE5 = #{spare5}
    

order by CREATED_TIME DESC

mapper.update:
update points_alliance.sys_user
CREATED_TIME = #{createdTime}, CREATOR = #{creator}, CREATOR_ID = #{creatorId}, LAST_UPDATE_ID = #{lastUpdateId}, LAST_UPDATE_TIME = #{lastUpdateTime}, LAST_UPDATER = #{lastUpdater}, NAME = #{name}, PHONE = #{phone}, ACCOUNT = #{account}, PASSWORD = #{password}, USER_STATE = #{userState}, ROLE_ID = #{roleId}, ROLE_NAME = #{roleName}, PARENT_ID = #{parentId}, COMPANY_ID = #{companyId}, MEMBER_LEVEL = #{memberLevel}, COMPANY_LEVEL = #{companyLevel}, BALANCE = #{balance}, CUMULATIVE_PROFIT = #{cumulativeProfit}, CUMULATIVE_POINT = #{cumulativePoint}, DELETED = #{deleted}, OPEN_ID = #{openId}, INVITE_CODE = #{inviteCode}, HEAD_URL = #{headUrl}, SPARE4 = #{spare4}, SPARE5 = #{spare5},
where ID = #{id}

mapper.delete:
delete from points_alliance.sys_user where ID
in

    #{id}

mapper.select:
select

from points_alliance.sys_user
where INVITE_CODE=#{inviteCode}
or PHONE=#{inviteCode}
